copy(mstartx, mouse.x);
copy(mstarty, mouse.y);
copy(lstartx, x);
copy(lstarty, y);set(layer[hzoom].visible,false);
asyncloop(pressed,
	calc(x, lstartx + mouse.x - mstartx);
	calc(y, lstarty + mouse.y - mstarty);
);

		calc(x_1,stagewidth / 2 + layer[img1].x); calc(x_1,stagewidth / 2 + layer[img2].x);  calc(x_1,stagewidth / 2 + layer[img3].x);
		calc(y_1,stageheight / 2 + layer[img1].y); calc(y_1,stageheight / 2 + layer[img2].y); calc(y_1,stageheight / 2 + layer[img3].y);
		copy(height_1,layer[img1].height);copy(height_1,layer[img2].height);copy(height_1,layer[img3].height);set(layer[hzoom].visible,false);
		if(iszoom,
			if(wheeldelta_touchscale GT 0,

            if(startzoom,
                set(startzoom,false);
                copy(start_wheeldelta_touchscale, wheeldelta_touchscale);
                copy(start_imagescale, layer[img1].scale);copy(start_imagescale, layer[img2].scale);copy(start_imagescale, layer[img3].scale);
                );
				div(tmp, wheeldelta_touchscale, start_wheeldelta_touchscale);
				clamp(layer[img1].scale, 1, 2.0); clamp(layer[img2].scale, 1, 2.0); clamp(layer[img3].scale, 1, 2.0);
				mul(layer[img1].scale, start_imagescale, tmp);mul(layer[img2].scale, start_imagescale, tmp); mul(layer[img3].scale, start_imagescale, tmp);
				,

				mul(sit,get(wheeldelta),0.015);
				mul(sit,layer[img1].scale);
				clamp(layer[img1].scale, 1, 2.0); clamp(layer[img2].scale, 1, 2.0); clamp(layer[img3].scale, 1, 2.0);
				add(layer[img1].scale,sit); add(layer[img2].scale,sit); add(layer[img3].scale,sit);

				);
          );

